import React, { useState, useEffect } from 'react';

const Salary = () => {
    const [salary, setSalary] = useState(0);
    const [additionalValues, setAdditionalValues] = useState([]);
    const [subtractValues, setSubtractValues] = useState([]);
    const [result, setResult] = useState(0);
    const [fullResult, setFullResult] = useState(0); // New state for full calculation result

    useEffect(() => {
        let total = salary;
        additionalValues.forEach(val => {
            if (val.enabled) {
                total += val.value;
            }
        });
        subtractValues.forEach(val => {
            total -= val.value;
        });
        setResult(total);

        // Calculate full result without considering enabled/disabled state
        let fullTotal = salary;
        additionalValues.forEach(val => {
            fullTotal += val.value;
        });
        subtractValues.forEach(val => {
            fullTotal -= val.value;
        });
        setFullResult(fullTotal);
    }, [salary, additionalValues, subtractValues]);

    return (
        <div>
            <h1>Salary Calculator</h1>
            <label>
                Salary:
                <input
                    type="number"
                    value={salary}
                    onChange={(e) => setSalary(parseFloat(e.target.value) || 0)}
                    placeholder="Enter salary"
                />
            </label>
            <h2>Earnings</h2>
            {additionalValues.map((item, index) => (
                <div key={index}>
                    <input
                        type="text"
                        value={item.label}
                        onChange={(e) => handleAdditionalLabelChange(index, e.target.value)}
                        placeholder={`Enter label for value ${index + 1}`}
                    />
                    <input
                        type="number"
                        value={item.value}
                        onChange={(e) => handleAdditionalValueChange(index, e.target.value)}
                        placeholder={`Enter value ${index + 1}`}
                    />
                    <button onClick={() => removeValueField(index)}>Delete</button>
                    <label>
                        <input
                            type="checkbox"
                            checked={item.enabled}
                            onChange={(e) => handleAdditionalEnabledChange(index, e.target.checked)}
                        />
                        Enable
                    </label>
                </div>
            ))}
            <div>
                <button onClick={addNewValueField}>Add New Allowance</button>
            </div>
            <h2>Deductions</h2>
            {subtractValues.map((item, index) => (
                <div key={index}>
                    <input
                        type="text"
                        value={item.label}
                        onChange={(e) => handleSubtractLabelChange(index, e.target.value)}
                        placeholder={`Enter label for subtract value ${index + 1}`}
                    />
                    <input
                        type="number"
                        value={item.value}
                        onChange={(e) => handleSubtractValueChange(index, e.target.value)}
                        placeholder={`Enter subtract value ${index + 1}`}
                    />
                    <button onClick={() => removeSubtractField(index)}>Delete</button>
                </div>
            ))}
            <div>
                <button onClick={addNewSubtractField}>Add New Deduction</button>
            </div>
            <p>Result (Considering Enabled/Disabled Additional Values): {result}</p>
            <p>Full Result (Without Considering Enabled/Disabled Additional Values): {fullResult}</p>
            <p>EPF 8: {result * 0.08}</p>
            <p>EPF 12: {result * 0.12}</p>
            <p>EPF 3: {result * 0.03}</p> {/* New output for EPF 3 */}
        </div>
    );
};

export default Salary;
