import React, { useState,useEffect } from 'react'
import './salary.css'
import { isValidInputTimeValue } from '@testing-library/user-event/dist/utils';

const Salary = () => {

    const [value1, setValue1] = useState(0);
    const [value2, setValue2] = useState(0);
    const [additionalValue, setAdditionalValue] = useState(0);
    const [showAdditionalInput, setShowAdditionalInput] = useState(false);
    const [sum, setSum] = useState(0);

    useEffect(() => {
      let total = value1 + value2;
      if (showAdditionalInput) {
        total += additionalValue;
      }
      setSum(total);
    }, [value1, value2, additionalValue, showAdditionalInput]);

    return(
        <div>
        <h1>Sum Calculator</h1>
        <input
          type="number"
          value={value1}
          onChange={(e) => setValue1(parseFloat(e.target.value) || 0)}
          placeholder="Enter first value"
        />
        <input
          type="number"
          value={value2}
          onChange={(e) => setValue2(parseFloat(e.target.value) || 0)}
          placeholder="Enter second value"
        />
        {showAdditionalInput && (
          <input
            type="number"
            value={additionalValue}
            onChange={(e) => setAdditionalValue(parseFloat(e.target.value) || 0)}
            placeholder="Enter additional value for first value"
          />
        )}
        <button onClick={() => setShowAdditionalInput(true)}>
          Add Another Value for First Value
        </button>
        <p>Sum: {sum}</p>
      </div>
    )
}

export default Salary
