import React, { useState } from 'react'
import './salary.css'
import { isValidInputTimeValue } from '@testing-library/user-event/dist/utils';

function Salary() {

  const [billDollarsInput, setBillDollarsInput] = useState("$0");
  const [tipPercentInput, setTipPercentInput] = useState("15%");
  const [numberOfPeopleInput, setNumberOfPeople] = useState("1");
  const [tipDollars, setTipDollars] = useState(0);
  const [billTotal, setBillTotal] = useState(0);
  const [isValidBill, setIsValidBill] = useState(true);
  const [isValidTip, setIsValidTip] = useState(true);
  const [isValidNumberOfPeople, setIsValidNumberOfPeople] = useState(true);

  function hadleBillDollarChange(e) {
    const input = e.target.value.replaceAll("$", "")

    setIsValidBill(isValidInput(input, ""))
  }

  return (
    <form action="" className="tip-calculator" onSubmit={() => null}>
      <div className="section">
        <div className="form-group">
          <label>Bill</label>
          <div className='counter'>
            <input 
              onChange={hadleBillDollarChange}
              value={billDollarsInput}
              type='text'
              placeholder='Enter your bill'
              className={isValidBill ? "" : 'error'}
            />
          </div>
        </div>
        <div className="form-group">
          <label>Tip</label>
          <div className='counter'>
            <button type="button"
              onChange={() => null}
              disabled={false}
            >
              -
            </button>
            <input 
              onChange={() => null}
              value={billDollarsInput}
              type='text'
              placeholder='Enter your bill'
              className={isValidBill ? "" : 'error'}
            />
            <button type="button"
              onChange={() => null}
            >
              +
            </button>
          </div>
        </div>
        <div className="form-group">
          <label>Tip</label>
          <div className='counter'>
            <button type="button"
              onChange={() => null}
              disabled={false}
            >
              -
            </button>
            <input 
              onChange={() => null}
              value={numberOfPeopleInput}
              type='text'
              placeholder='Enter your bill'
              className={numberOfPeopleInput ? "" : 'error'}
            />
            <button type="button"
              onChange={() => null}
            >
              +
            </button>
          </div>
        </div>
      </div>
      <div className='section'>
        <div className='result-container'>
          <div className='header-and-result'>
          <p>Tip:</p>
          <p>-</p>
          </div>
          <p className='per-person'>/person</p>
        </div>
        <div className='result-container'>
          <div className='header-and-result'>
            <p>Total:</p>
            <p>-</p>
          </div>
          <p className='per-person'>/person</p>
        </div>
      </div>
    </form>
  )
}

export default Salary
