import React, { useState,useEffect } from 'react'
import './salary.css'
import { isValidInputTimeValue } from '@testing-library/user-event/dist/utils';

const Salary = () => {
    const [salary, setSalary] = useState(0);
    const [additionalValues, setAdditionalValues] = useState([]);
    const [sum, setSum] = useState(0);

    useEffect(() => {
      let total = salary;
      additionalValues.forEach(val => {
        total += val.value;
      });
      setSum(total);
    }, [salary, additionalValues]);

    const handleAdditionalValueChange = (index, newValue) => {
      const updatedValues = [...additionalValues];
      updatedValues[index].value = parseFloat(newValue) || 0;
      setAdditionalValues(updatedValues);
    };

    const handleLabelChange = (index, newLabel) => {
      const updatedValues = [...additionalValues];
      updatedValues[index].label = newLabel;
      setAdditionalValues(updatedValues);
    };

    const addNewValueField = () => {
      setAdditionalValues([...additionalValues, { label: '', value: 0 }]);
    };

    const removeValueField = (index) => {
      const updatedValues = [...additionalValues];
      updatedValues.splice(index, 1);
      setAdditionalValues(updatedValues);
    };





    return(
        <div>
      <h1>Sum Calculator</h1>
      <label>
        Salary:
        <input
          type="number"
          value={salary}
          onChange={(e) => setSalary(parseFloat(e.target.value) || 0)}
          placeholder="Enter salary"
        />
      </label>
      {additionalValues.map((item, index) => (
        <div key={index}>
          <input
            type="text"
            value={item.label}
            onChange={(e) => handleLabelChange(index, e.target.value)}
            placeholder={`Enter label for value ${index + 1}`}
          />
          <input
            type="number"
            value={item.value}
            onChange={(e) => handleAdditionalValueChange(index, e.target.value)}
            placeholder={`Enter value ${index + 1}`}
          />
          <button onClick={() => removeValueField(index)}>Delete</button>
        </div>
      ))}
      <div>
        <button onClick={addNewValueField}>Add Another Value</button>
      </div>
      <p>Sum: {sum}</p>
    </div>
    )
}

export default Salary
